package com.aerosimo.mail;

import com.aerosimo.util.Log;
import com.aerosimo.util.SendEmail;

public class PasswordUpdateMail {
    public static String sendPasswordMail(String userName, String emailAddress) {
        StringBuilder message;
        String response;
        message = new StringBuilder("<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\n" +
                "<html xmlns=\"http://www.w3.org/1999/xhtml\">\n" +
                "<body>\n" +
                "    <style></style>\n" +
                "    <spacer size=\"16\"></spacer>\n" +
                "    <container>\n" +
                "        <row class=\"header\" style=\"background:#6C6C6C\">\n" +
                "            <columns>\n" +
                "                <spacer size=\"16\"></spacer>\n" +
                "            </columns>\n" +
                "        </row>\n" +
                "        <row>\n" +
                "            <columns>\n" +
                "               \n" +
                "                <spacer size=\"16\"></spacer>\n" +
                "\t\t\t\t<br>\n" +
                "\t\t\t\t<br>\n" +
                "\t\t\t\t<h1 class=\"text-center\" style=\"color:#01252F;font-family:Lato,sans-serif;font-size:30pt;text-align:center\">Hi ");
        message.append(userName);
        message.append("</h1>\n" +
                "\t\t\t\t<br>\n" +
                "\t\t\t\t<center><img src='data:image/png;base64,'/></center>\n" +
                "\t\t\t\t<br>\n" +
                "\t\t\t\t<br>\n" +
                "\t\t\t\t<p class=\"textcenter\" style=\"font-family:Lato,sans-serif;text-align:center\">If you did not request your password change, please let us know immediately by contacting the administrator.</p>\n" +
                "                <p style=\"font-family:Lato,sans-serif\"></p>\n" +
                "            </columns>\n" +
                "        </row>\n" +
                "        <spacer size=\"16\"></spacer>\n" +
                "    </container>\n" +
                "</body>\n" +
                "</html>");
        response = SendEmail.sendMail(emailAddress, "Password Reset Successfully", message.toString(), "");
        Log.info("Email response is: " + response);
        return response;
    }
}